import React, { useState } from 'react';
import axios from 'axios';



class Test extends React.Component{
    state = {
        posts:[]
    }

    //Calls whenever the React Component mounts.
    componentDidMount = () => {
      this.getQuizes();
    }

    getQuizes = () => {
        axios.get('http://localhost:8080/api/quiz')
            .then((response) => {
                const data = response.data;
                this.setState({posts: data})
                console.log("Data retrieved!");
            })
            .catch((e) => {
              console.log("Error!", e)
            })
    }

    displayQuizes = (posts) => {
        if (!posts.length){return null;}

        return posts.map((post, index) => (
          <div key={index}>
            <a href='' className='d-flex btn btn-dark justify-left w-100 h-100 text-left'>
              <div className='align-center text-left d-flex w-100 h-100 border'>
                <p className='align-center'>{post.title} Uploaded: {post.date} | Creator: {post.creator}</p>
              </div>
            </a>
          </div>
        ));
    };

    handleEventUpdate = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;

        this.setState({
          [name]: value
        })

      };

    render(){
        //React.useLayoutEffect(() => {
        //    NavigationPreloadManager.setOptions({headerShown: false});
        //});
        // the above code could be used in some way to remove the nav bar from the top of the page

        let name = "bob" // maybe pass in the user's name from the database? 
        return (
            <>
            <div className='text-center text-white'>
                <h1>Dashboard</h1>
                <br></br>
                <h5> Here you can view quiz responses</h5>
                <h5> & create a quiz</h5>
                <br></br>
                <div className='d-flex'>
                    <div className='w-25'>
                        <h3>Welcome Back</h3>
                        <p>{name}</p>
                        <a href='/quiz-creator' className='btn btn-secondary'>Create A Quiz</a>
                    </div>
                    <div className='w-100'>
                        <h3>All Quizes</h3>
                        <div className='quizs'>
                        {this.displayQuizes(this.state.posts)}
                        </div>
                    </div>
                </div>
            </div>
            </>
        );
    }
}

export default Test;
