import React, { useState } from 'react';
import axios from 'axios';

class Exam extends React.Component{
    state = {
        quizID:'',
        posts:[]
    }


    getQuiz = () => {
        axios.get('http://localhost:8080/api/quiz/selection')
            .then((response) => {
                const data = response.data;
                this.setState({posts: data})
                console.log("Data retrieved!");
            })
            .catch((e) => {
              console.log("Error!", e)
            })
    }

    displayQuiz = (posts, role) => {
        if (!posts.length){return null;}
        if(role=='admin'){
            return posts.map((post, index) => (
                <form id={post._id} onSubmit={this.redirectToQuiz()} autoComplete="off">
                <div key={index}>

                    <div className='align-center text-left'>
                        <div className='flex-column'><h3 className='align-center'>{post.title}</h3>
                            <p></p></div>
                      <p className='align-center'>Uploaded: {post.date} by {post.creator}</p>
                      <input
                          type='hidden'
                          name='quizID'
                          value={post._id}
                          onChange={this.handleEventUpdate}
                          required
                      />
                      <button id={post._id} type="button" className="btn btn-secondary">Load</button>
                      <br></br><br></br>
                    </div>
                </div>
                </form>
              ));
        }else{
            return posts.map((post, index) => (
                <div key={index}>
                  <a id={post._id} className='d-flex btn btn-dark justify-left text-left'>
                    <div className='align-center text-left d-flex'>
                      <p className='align-center'>{post.title} | Uploaded: {post.date} | Creator: {post.creator}</p>
                    </div>
                  </a>
                </div>
              ));
        }
    };

    handleEventUpdate = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;

        this.setState({
          [name]: value
        })
        console.log(this.state);
      };


    render(){

    }
}

export default Exam;
