/* exam.js
 * This page is responsible for the actual exam portion.
 * Should grab a quiz from the database then give the user to solve questions
 * 
 * Daniel
 */

import React from 'react';
import axios from 'axios';

import { API_BASE_URL } from '../config';

class Exam extends React.Component{
    state = {
        posts:[],
        selectedOptions: {},
        error: null
    }

    // Calls whenever the React Component mounts.
    componentDidMount = () => {
      const search = window.location.search;
      const params = new URLSearchParams(search);
      const quizID = params.get('id');
      this.getQuiz(quizID);
    }

    // Get a quiz
    getQuiz = async (quizID) => {
      axios.get(`${API_BASE_URL}/quiz/exam?id=${quizID}`)
        .then((response) => {
          const data = response.data;
          const dataArray = data ? [data] : [];
          this.setState({ posts: dataArray })
        })
        .catch((error) => {
          console.log("Error!", error);
        });
    }

    // Returns a map of posts.
    displayQuiz = (quizzes) => {
        if (!quizzes) {
          return (<div>Error! Unable to display quizzes.</div>); // Return early if quizzes is not an array
        }

        const handleOptionChange = (questionIndex, optionIndex) => {
          this.setState(prevState => ({
            selectedOptions: {
              ...prevState.selectedOptions,
              [questionIndex]: optionIndex
            }
          }));
        };

        return quizzes.map((quiz, index) => (
          <div id={index} key={index}>
            <form>
              <h1 id={quiz.title}>{quiz.title}</h1>
              <p id={quiz.creator}>By {quiz.creator}</p>
              {quiz.questions.map((question, questionIndex) => (
                <div key={questionIndex}>
                  <p>#{questionIndex + 1}: {question.question}</p>
                  {question.options.map((option, optionIndex) => (
                    <div className="radio" key={optionIndex}>
                      <label>
                        <input type="radio" name={`q${questionIndex + 1}`} value={`option${optionIndex + 1}`} />
                        {option}
                      </label>
                    </div>
                  ))}
                  <br></br>
                </div>
              ))}
              <button type="button" className="btn btn-outline-light">Submit</button>
            </form>
          </div>
        ));
      }

    // Updates the state of the target.
    handleEventUpdate = (event) => {
        const target = event.target;
        const name = target.name; 
        const value = target.value;

        this.setState({
          [name]: value
        })
      };


    // Renders quiz
    render() {
      const { posts, error } = this.state;
        return (
          <div>
            {error && <p>Error: {error.message}</p>}
            <div className='text-center text-white container'>

              {this.displayQuiz(posts)}
            </div>
          </div>
        );
      }
}

export default Exam;
