import React, { useState } from 'react';
import axios from 'axios';

class Exam extends React.Component{
    state = {
        quizID:'',
        posts:[]
    }

    // Calls whenever the React Component mounts.
    componentDidMount = () => {
        this.state.quizID = localStorage.getItem('quizID')
        this.getQuiz();
      }


    // Get a quiz
    getQuiz = (id) => {
        axios.get('http://localhost:8080/api/quiz/exam', {params:{quizID: id}})
            .then((response) => {
                const data = response.data;
                this.setState({posts: data})
                console.log("Data retrieved!", data);
            })
            .catch((e) => {
              console.log("Error!", e)
            })
    }

    // Returns a map of posts.
    displayQuiz = (posts) => {
        const map = posts.map((post, index) => (

            <div id={index} key={index}>
                <p>{post.questions}</p>
            </div>
            ));
        return map;
    }


    // Updates the state of the target.
    handleEventUpdate = (event) => {
        const target = event.target;
        const name = target.name; 
        const value = target.value;

        this.setState({
          [name]: value
        })
        console.log(this.state);
      };


    // Renders quiz
    render(){;
        const title = 'Test';
        const creator = 'test';
        return(
            <div className='text-center text-white'>
                <h1>{title}</h1>
                <p>by {creator} </p>
                <div>
                    {this.displayQuiz(this.state.posts)}
                </div>
            </div>
        );
    }
}

export default Exam;
