/* analytics.js
 * This page is responsible for displaying all of the quiz responses
 * This page should show respondents score, as well as a few other things. 
 * 
 * Daniel
 */

import React from 'react';
import axios from 'axios';
import { API_BASE_URL } from '../config';

import "./css/quiz-create.css" //going to include analytics css in this file

class Results extends React.Component{

    state = {
        results:[],
        quiz:[],
        error: null
    }

    //Calls whenever the React Component mounts.
    componentDidMount = () => {
        const search = window.location.search;
        const params = new URLSearchParams(search);
        const resultID = params.get('id');
        this.getQuizResult(resultID);
    }

    getQuizResult = async (resultID) => {
      await axios.get(`${API_BASE_URL}/result?id=${resultID}`)
        .then((res) => {
          const data = res.data;
          const dataArray = data ? [data] : [];
          this.setState({ results: dataArray });
          if (dataArray.length > 0) {
            const quizID = dataArray[0].quizid;
            this.getQuiz(quizID);
          }
        })
        .catch((err) => {
          console.log("Error!", err);
        });
    }

    getQuiz = async (quizID) => {
      axios.get(`${API_BASE_URL}/exam?id=${quizID}`)
        .then((response) => {
          const data = response.data;
          const dataArray = data ? [data] : [];
          this.setState({ quiz: dataArray })
        })
        .catch((error) => {
          console.log("Error!", error);
        });
    }

    displayQuizResult = (result) => {
      if (!result) {
        return <div>Error! Unable to display results.</div>; // Return early if result is not available
      }

      return (
        <div>


          {/* Add gauge component to display percentage */}
        </div>
      );
    }

    render() {
      const error = this.state.error;
      const quiz = this.state.quiz;
      const results = this.state.results;
      console.log("Quiz", this.state.quiz)
      return (
        <div className='text-center text-white container'>
          <h1>Your Result</h1>
          {error ? (
            <div>{error}</div>
          ) : (
            <div className='text-center text-white'>
              <h2>{quiz.title}</h2>
              {this.displayQuizResult(results)}
            </div>
          )}
        </div>
      )
    }
  }

export default Results;
