import React, { useState } from 'react';
import axios from 'axios';

class Exam extends React.Component{
    state = {
        quizID:'',
        posts:[]
    }

    componentDidMount = () => {
        this.state.quizID = localStorage.getItem('quizID')
        this.getQuizes();
      }


    getQuiz = () => {
        axios.get('http://localhost:8080/api/quiz/exam')
            .then((response) => {
                const data = response.data;
                this.setState({posts: data})
                console.log("Data retrieved!");
            })
            .catch((e) => {
              console.log("Error!", e)
            })
    }

    displayQuiz = (posts, role) => {
        return posts.map((post, index) => (
            <div key={index}>
                <a id={post._id} className='d-flex btn btn-dark justify-left text-left'>
                <div className='align-center text-left d-flex'>
                    <p className='align-center'>{post.title} | Uploaded: {post.date} | Creator: {post.creator}</p>
                </div>
                </a>
            </div>
            ));
        }


    handleEventUpdate = (event) => {
        const target = event.target;
        const name = target.name;
        const value = target.value;

        this.setState({
          [name]: value
        })
        console.log(this.state);
      };


    render(){
        return(
            <div>{this.displayQuiz}</div>
        );
    }
}

export default Exam;
